<div id="calculator-container">
<div>
<label for="m1">m1:</label>
<input type="number" id="m1" value="4">
</div><div>
<label for="m2">m2:</label>
<input type="number" id="m2" value="5">
</div><div>
<label for="n1">n1:</label>
<input type="number" id="n1" value="2">
</div><div>
<label for="n2">n2:</label>
<input type="number" id="n2" value="3">
</div><div>
<label for="x">x:</label>
<input type="number" id="x" value="0.5">
</div>
<div>
<label for="formula1">formula1:</label>
<input type="text" id="formula1" value="n1 &#43; m1">
<span id="result-formula1" class="result-display"></span>
</div>
<div>
<label for="formula2">formula2:</label>
<input type="text" id="formula2" value="n2 * m2">
<span id="result-formula2" class="result-display"></span>
</div>
<div>
<label for="formula3">formula3:</label>
<input type="text" id="formula3" value="Math.asin(x) * 180 / Math.PI">
<span id="result-formula3" class="result-display"></span>
</div>
<button type="button" onclick="calculate()">Solve</button>
</div>
<script>
function calculate() {
const container = document.getElementById('calculator-container');
const inputs = container.querySelectorAll('input[type="number"]');
const formulas = container.querySelectorAll('input[type="text"]');
const vars = {};
inputs.forEach(input => {
const varName = input.id;
vars[varName] = input.value === "" ? "0" : input.value;
});
formulas.forEach(formulaInput => {
let formula = formulaInput.value;
Object.keys(vars).forEach(varName => {
const re = new RegExp('\\b' + varName + '\\b', 'g');
formula = formula.replace(re, vars[varName]);
});
const resultSpan = document.getElementById('result-' + formulaInput.id);
try {
const finalAnswer = eval(formula);
resultSpan.textContent = ` = ${finalAnswer}`;
} catch (error) {
resultSpan.textContent = ` = Error`;
}
});
}
</script>
