type="number"
id="num2-3"
value="10"
placeholder="Second number"
>
<button onclick="addNumbers( 3 )">
Add Now
</button>
<div id="result-3" class="result-display"></div>
</div>
<script>
function addNumbers(uid) {
const num1 = parseFloat(document.getElementById(`num1-${uid}`).value) || 0;
const num2 = parseFloat(document.getElementById(`num2-${uid}`).value) || 0;
document.getElementById(`result-${uid}`).textContent = `Result: ${num1 + num2}`;
}
</script>
<style>
.add-container {
margin: 1rem 0;
padding: 1rem;
border: 1px solid #ddd;
}
.result-display {
margin-top: 0.5rem;
font-weight: bold;
}
</style>
